import Joi from '@hapi/joi';

const AndroidBuildConfigSchema = Joi.object().keys({
  keystore: Joi.object().keys({
    keystorePath: Joi.string().required(),
    keystorePassword: Joi.string().required(),
    keyAlias: Joi.string().required(),
    keyPassword: Joi.string().required(),
  }).required(),
});

const iOSBuildConfigSchema = Joi.object().keys({
  provisioningProfilePath: Joi.string().required(),
  distributionCertificate: Joi.object().keys({
    path: Joi.string().required(),
    password: Joi.string().required(),
  }).required(),
});

const BuildConfigSchema = Joi.object().keys({
  android: AndroidBuildConfigSchema,
  ios: iOSBuildConfigSchema,
});

export default BuildConfigSchema;
