import last from 'lodash/last';

import fastlane from '../fastlane';

describe('fastlane function', () => {
  it('should run specified command', async () => {
    const { stdout } = await fastlane(['spaceship', '--version']);
    const lastLine = last(stdout.trim().split('\n'));
    expect(lastLine).toMatch(/^spaceship [0-9\.]+$/);
  });
});
